void day1(string input)
{
    vector<int> firstList;
    vector<int> secondList;

    stringstream ss(input);
    int locationId;
    bool toTheFirstList = true;
    while (ss >> locationId)
    {
        if (toTheFirstList)
        {
            toTheFirstList = false;
            firstList.push_back(locationId);
        }
        else
        {
            toTheFirstList = true;
            secondList.push_back(locationId);
        }
    }

    stable_sort(firstList.begin(),firstList.end());
    stable_sort(secondList.begin(),secondList.end());

    //part 1
    int totalDistance = 0;

    int counter = 0;
    while(firstList.size() > counter) {
        int firstLocation = firstList[counter];
        int secondLocation = secondList[counter];

        int difference;
        if (firstLocation > secondLocation)
            difference = firstLocation - secondLocation;
        else
            difference = secondLocation - firstLocation;

        totalDistance += difference;
        counter++;
    }

    cout << totalDistance << endl;


    //part 2
    int totalSimilarityScore = 0;

    int firstListCounter = 0;
    int secondListCounter = 0;
    while(firstList.size() > firstListCounter) {
        int firstLocation = firstList[firstListCounter];
        int occurences = 0;
        int secondListCounter = 0;

        while (secondList.size() > secondListCounter)
        {
            if (firstLocation == secondList[secondListCounter])
                occurences++;

            secondListCounter++;
        }

        totalSimilarityScore += firstLocation * occurences;
        firstListCounter++;
    }

    cout << totalSimilarityScore << endl;
}

